  ADD CONSTRAINT PLACE_ibfk_1 FOREIGN KEY (GCUTOURID) REFERENCES GCUTOUR (GCUTOURID);

DROP TABLE IF EXISTS DESTINATIONIMAGES;
CREATE TABLE IF NOT EXISTS DESTINATIONIMAGES (
  PLACEIMAGEID int(11) NOT NULL AUTO_INCREMENT,
  PLACEIMAGEURL varchar(30) NOT NULL,
  PLACEIMAGETEXT varchar(30),
  PLACEID int(11) NOT NULL,
  PRIMARY KEY (PLACEIMAGEID)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;

